mapper.delete:
DELETE
  FROM cart
 WHERE user_id = #{userId}
   AND product_number = #{productNumber};

mapper.insert:
INSERT
    INTO cart( user_id
             , product_order_count
             , product_number
             , product_name
             , product_price
             , product_main_image
             )
VALUES ( #{userId}
       , #{productOrderCount}
       , #{cartProduct.productNumber}
       , #{cartProduct.productName}
       , #{cartProduct.productPrice}
       , #{cartProduct.productMainImage}
       )

mapper.select:
SELECT *
  FROM cart
 WHERE user_id = #{userId}
   AND product_number = #{cartProduct.productNumber};

SELECT * FROM ( SELECT inner_table.* , @ROWNUM := @ROWNUM+1 AS row_seq FROM ( SELECT * FROM cart
user_id = #{searchKeyword}
ORDER BY cart_number DESC ) inner_table WHERE (@ROWNUM:=0)=0 limit #{endRowNum} ) result_table WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}

SELECT COUNT(*)
FROM( SELECT *
FROM cart

    user_id = #{searchKeyword}

) countTable
