mapper.select:
SELECT * 
FROM image 
WHERE image_classification_code = 'P' AND image_classification_number = #{value}

SELECT * FROM ( SELECT inner_table.* , @ROWNUM := @ROWNUM+1 AS row_seq FROM ( SELECT p.* FROM product p
product_group_number is null
AND p.product_status IN (${searchKeyword})
AND p.product_name LIKE '%'||#{searchKeyword}||'%'
AND p.product_category_code = #{searchKeyword}
AND p.reg_date BETWEEN #{beforeDate} and #{afterDate}
ORDER BY ${orderName} ) inner_table WHERE (@ROWNUM:=0)=0 limit #{endRowNum} ) result_table WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}

  	SELECT COUNT(*)
  	FROM(	SELECT p.*
			FROM product p
			
				product_group_number is null
				
				
					
		 				AND p.product_status IN (${searchKeyword})
					 
					
		 				AND p.product_name LIKE '%'||#{searchKeyword}||'%'
															
					
		 				AND p.product_category_code = #{searchKeyword}
																								
				
				
					AND p.reg_date BETWEEN #{beforeDate} and #{afterDate}
				
			
		) countTable

mapper.update:
UPDATE product
product_name = #{productName}, price = #{price}, product_main_image = #{productMainImage}, product_detail = #{productDetail:LONGVARCHAR}, product_category_code = #{categoryCode}, product_quantity = #{productQuantity}, product_status = #{productStatus}, product_group_number = null #{product_group_number}
WHERE product_number = #{productNumber}

UPDATE product
SET product_status = #{productStatus}
WHERE product_number = #{productNumber}
