mapper.insert:
INSERT INTO `order` ( product_number
                    , buyer_id
                    , store_id
                    , order_bundle_number
                    , order_date
                    , order_quantity
                    , order_status
                    , receiver_name
                    , receiver_phone
                    , receiver_address
                    , receiver_request
                    , delivery_tracking_number
                    , delivery_company_code
                    , payment_option
                    , total_payment_price
                    , reward_point
                    , using_point
                    , order_claim_request_date
                    , order_claim_response_date
                    , order_claim_reason
                    )
VALUES ( #{product_number}
       , #{buyer_id}
       , #{store_id}
       , #{order_bundle_number}
       , #{order_date}
       , #{order_quantity}
       , #{order_status}
       , #{receiver_name}
       , #{receiver_phone}
       , #{receiver_address}
       , #{receiver_request}
       , #{delivery_tracking_number}
       , #{delivery_company_code}
       , #{payment_option}
       , #{total_payment_price}
       , #{reward_point}
       , #{using_point}
       , #{order_claim_request_date}
       , #{order_claim_response_date}
       , #{order_claim_reason}
       );

mapper.update:
UPDATE `order`
   SET product_number            = #{product_number}
     , buyer_id                  = #{buyer_id}
     , store_id                  = #{store_id}
     , order_bundle_number       = #{order_bundle_number}
     , order_date                = #{order_date}
     , order_quantity            = #{order_quantity}
     , order_status              = #{order_status}
     , receiver_name             = #{receiver_name}
     , receiver_phone            = #{receiver_phone}
     , receiver_address          = #{receiver_address}
     , receiver_request          = #{receiver_request}
     , delivery_tracking_number  = #{delivery_tracking_number}
     , delivery_company_code     = #{delivery_company_code}
     , payment_option            = #{payment_option}
     , total_payment_price       = #{total_payment_price}
     , reward_point              = #{reward_point}
     , using_point               = #{using_point}
     , order_claim_request_date  = #{order_claim_request_date}
     , order_claim_response_date = #{order_claim_response_date}
     , order_claim_reason        = #{order_claim_reason}
 WHERE order_number = 1;
